import { useState, useEffect} from 'react'

const Search = () => {
  const [inputError, setInputError] = useState()

  const validateNSN = (event)=>{
    let input = event.target.value
    
    if(isNaN(+input)){
      setInputError('Input as 8415-00-782-2889 or 8415007822889')
    }else{
      let removeDash = input.match(/(\d{4})(\d{2})(\d{3})(\d{4})/) || input.match(/(\d{4})-(\d{2})-(\d{3})-(\d{4})/)
      
      console.log(removeDash)

      setInputError()
    }
  }

  return (
    <form action="/search" method="post" name="nsnSearchForm">
      <ul className="list-plain list-search">
        <li>
          <input type="text" onChange={event => validateNSN(event)} className="pull-left input-md form-control input-abg" placeholder="Search using NSN..."/>
        </li>
        <li>
          <button type="submit" className="btn btn-md btn-abg pull-left">Search</button>
        </li>
      </ul>
      {inputError ? <span className='input-error'><span style={{color:'red', fontWeight:'bold'}}>Error </span>{inputError}</span> : undefined}

    </form>
    );
}
 
export default Search;
