import React, {useEffect, useState} from 'react'
import LoadingPlane from '../LoadingPlane'
import NSNList from './NSNList'
import ServerError from '../ServerError'

const RandomLayout = () => {
  const [loading, setLoading] = useState(false)
  const [randomNsn, setRandomNsn] = useState()
  const [displayError, setDisplayError] = useState(false)
  const [errorMessage, setErrorMessage] = useState()
  const fetchRandom = ()=> {
    // fetches 15 random nsn from data base 
      setLoading(true)
      fetch('/api/random_list')
      .then(res => {
        let response = res.json()
        console.log(res)
        if(res.status !== 200){
          setDisplayError(true)
          setErrorMessage({code: response.code, message: response.message})
          throw new Error('Error in fetchRandom')
        }
        return response
      })
      .then(data => {
        console.log(data)
        setRandomNsn(data)
        setLoading(false)
        setDisplayError(false)
        // setErrorMessage()
      })
      .catch(err => {
        setLoading(false)
        // setDisplayError(true)
      })
  }

  useEffect(()=>{
    fetchRandom()
  }, [])

  return ( 
    <div className='random-list'>
      <h1>Random List of 15 NSNs</h1>
      {loading ? <LoadingPlane /> : displayError ? <ServerError error={errorMessage} /> : <NSNList randomNsn={randomNsn} />}
    </div>
   );
}
 
export default RandomLayout;
