import React, {useEffect, useState} from 'react'
import LoadingPlane from '../LoadingPlane'
import NSNList from './NSNList'

const RandomLayout = () => {
  const [loading, setLoading] = useState(false)
  const [randomNsn, setRandomNsn] = useState()
  const [displayError, setDisplayError] = useState(false)

  const fetchRandom = ()=> {
    // fetches 15 random nsn from data base 
      setLoading(true)
      fetch('/api/random_list')
      .then(res => res.json())
      .then(data => {
        setRandomNsn(data)
        setLoading(false)
        setDisplayError(false)
      })
      .catch(err => {
        console.log(err)
        setLoading(false)
        setDisplayError(true)
      })
  }

  // const nsnList = (
  //   <ul>
  //     {randomNsn ? randomNsn.map(obj => {
  //         return (
  //           <li className='nsn-item'>
  //             <a href={`/nsn/${obj.nsn}`} className='nsn-link'>
  //               NSN &nbsp;{obj.nsn}
  //             </a>
  //           </li>
  //         )
  //       }) 
  //     : undefined}
  //   </ul>
  // )

  useEffect(()=>{
    fetchRandom()
  }, [])

  return ( 
    <div className='random-list'>
      <h1>Random List of 15 NSNs</h1>
      {loading ? <LoadingPlane /> : <NSNList randomNsn={randomNsn} />}
    </div>
   );
}
 
export default RandomLayout;
